Method.Implementation:
d $system.Process.SetZEOF(1) s tStatement = ##class(%SQL.Statement).%New() s tSC=tStatement.%PrepareClassQuery("%Library.File","FileSet") s rs=tStatement.%Execute("C:\Users\Paul\Desktop\Test-Patients\patients","*.json",,1) while rs.%Next() { s tFile=rs.%Get("Name") w !,tFile i $p(tFile,".",2)'="txt",$p(tFile,".",2)'="json" continue k ^||TJSON w !,"loading!" Open tFile:"R" For { Use tFile R tStr Q:$zeof D ..JSON(tStr) } Close tFile
D ..AddGP()
F i=1:1:$o(^||TJSON(""),-1) W !,^||TJSON(i) r *y d ..POSTPUT("?","Patient") r *y }

// ^ODSINDX2(pResource,pODSCode)
Set tODSCode=""
For {
	Set tODSCode=$Order(^ODSINDX2("Practitioner",tODSCode))
	Quit:tODSCode=""
	I $E(tODSCode)="C" W !,tODSCode
}

K ^LOG(pFile) S ^LOG(pFile,"start")=$H
S tODSCode="" For { Set tODSCode=$Order(^CSVDATA(pFile,tODSCode)) Quit:tODSCode=""
;I tODSCode'="07J" CONTINUE
S R=^CSVDATA(pFile,tODSCode) S tStatus=$P(R,"~",1),tJoinedDate=$P(R,"~",2),tLeftDate=$P(R,"~",3) S tParentOrg=$P(R,"~",4),tName=$P(R,"~",5),tAdd1=$P(R,"~",6) S tAdd2=$P(R,"~",7),tAdd3=$P(R,"~",8),tAdd4=$P(R,"~",9) S tAdd5=$P(R,"~",10),tPostCode=$P(R,"~",11),tCounty=$P(R,"~",12) S tStartDate=$P(R,"~",13),tEndDate=$P(R,"~",14)
Kill ^||TJSON Do ..JSON("{""resourceType"": ""Organization"",") //Do ..JSON("""id"": """_tODSCode_""",")
// Start date e.g 20181001 2019-01-28 Set tStartDate=$E(tStartDate,1,4)_"-"_$E(tStartDate,5,6)_"-"_$E(tStartDate,7,8) // Open Date If tEndDate'="" Set tEndDate=$E(tEndDate,1,4)_"-"_$E(tEndDate,5,6)_"-"_$E(tEndDate,7,8) // Close Date
Do ..JSON("""extension"": [{") Do ..JSON("""valuePeriod"":{")
If tEndDate="" { Do ..JSON("""start"": """_tStartDate_"""}}],") }
If tEndDate'="" { Do ..JSON("""start"": """_tStartDate_""",") Do ..JSON("""end"": """_tEndDate_"""}}],") }
Set tStatus=$Select(tStatus="A":"true",1:"false")
Do ..JSON("""active"":"_tStatus_",") Do ..JSON("""name"":"""_tName_""",")
Do ..JSON("""address"": [{") Do ..JSON("""line"": [") Do ..JSON(""""_tAdd1_""",") Do ..JSON(""""_tAdd2_"""],") Do ..JSON("""city"": """_tAdd3_""",") Do ..JSON("""district"": """_tCounty_""",") Do ..JSON("""postalCode"": """_tPostCode_"""}],")
// Org2Org If tParentOrg'="",$Data(^ODSINDX2("Organization",tParentOrg)) { Set tRef=$P(^ODSINDX2("Organization",tParentOrg),"/",6) Do ..JSON("""partOf"": {") Do ..JSON("""reference"": ""Organization/"_tRef_"""") Do ..JSON("},") }
Do ..JSON("""identifier"": [{") Do ..JSON("""system"": ""https://fhir.nhs.uk/Id/ods-organization-code"",") Do ..JSON("""value"": """_tODSCode_"""") Do ..JSON("}]}")
/* ;If tEndDate'="" { F i=1:1:$o(^||TJSON(""),-1) W !,^||TJSON(i) R *Y ;} */
F i=1:1:$o(^||TJSON(""),-1) W !,^||TJSON(i) R *Y
D ..POSTPUT(tODSCode, "Organization") R *Y }
S ^LOG(pFile,"end")=$H

S tFile=$P($Piece(pFile,"\",$Length(pFile,"\")),".") Kill ^CSVDATA(tFile)
Set tSQL = "" For i = 1:1:27 { Set tSQL=tSQL_"Field"_i_" VARCHAR(100)"_$S(i<27:",",1:"") } Do ##class(%SQL.Util.Procedures).CSV(2,tSQL,pFile) If %sqlcontext.%SQLCODE<0 { #dim tEx As %Exception.SQL Set tEx = ##class(%Exception.SQL).CreateFromSQLCODE(%sqlcontext.%SQLCODE, %sqlcontext.%Message) }
Set tRecordSet = %sqlcontext.NextResult()
While tRecordSet.%Next() { Set tODSCode=tRecordSet.%Get("Field1") Set tStatus=tRecordSet.%Get("Field13") Set tJoinedDate=tRecordSet.%Get("Field16") Set tLeftDate=tRecordSet.%Get("Field17") Set tParentOrg=tRecordSet.%Get("Field15") Set tName=tRecordSet.%Get("Field2") Set tAdd1=tRecordSet.%Get("Field5") // THE HEALTH CENTRE Set tAdd2=tRecordSet.%Get("Field6") // LAWSON STREET Set tAdd3=tRecordSet.%Get("Field7") // STOCKTON-ON-TEES Set tAdd4=tRecordSet.%Get("Field8") // CLEVELAND Set tAdd5=tRecordSet.%Get("Field9") // <COUNTY> Set tPostCode=tRecordSet.%Get("Field10") // TS18 1HU Set tOpenDate=tRecordSet.%Get("Field11") // OpenDate Set tCloseDate=tRecordSet.%Get("Field12") // CloseDate
Set tCounty=tAdd5 If tAdd5="" Set tCounty=tAdd4
// Status not supported in Organization files If tStatus="" { S tStatus="A" If tCloseDate'="" S tStatus="C" }
Set R=tStatus_"~"_tJoinedDate_"~"_tLeftDate_"~"_tParentOrg_"~"_tName_"~" Set R=R_tAdd1_"~"_tAdd2_"~"_tAdd3_"~"_tAdd4_"~"_tAdd5_"~"_tPostCode_"~"_tCounty_"~" Set R=R_tOpenDate_"~"_tCloseDate
Set ^CSVDATA(tFile,tODSCode)=R
Write !,tODSCode }
